
addCardToCatalog().then(() => {
    const clickToCard = document.querySelectorAll(".card");
    clickToCard.forEach(element => {
        element.addEventListener("click", () => {
            modal_window(element);
            body.style.overflow = "hidden";

        })

    })
    var swiper = new Swiper(".card-swiper",{effect:"cards",grabCursor:!0,pagination:{el:".swiper-pagination",type:"fraction"}});swiper=new Swiper(".paralax-swiper",{direction:"horisontal",slidesPerView:1});


});